parameters.jsCode:
let missing = [];
if (!$json.output.origin) missing.push("origin city");
if (!$json.output.destination) missing.push("destination city");
if (!$json.output.departure_date) missing.push("departure date");
if (!$json.output.travelers) missing.push("travelers");
return [{
  json: {
    missing: missing.length ? `Please provide: ${missing.join(", ")}` : ""
  }
}];

